import React, { useEffect, useState, ChangeEvent } from 'react';
import { Link } from "react-router-dom";
import { FiArrowLeft } from "react-icons/fi";
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import api from "../../services/api";
import axios from 'axios';

import './styles.css';
import '../../responsive.css'
import '../../App.css'
import logo from '../../assets/logo.svg';
import { Interface, createInterface } from 'readline';
import { marker } from 'leaflet';
import { makeLoadScriptUrl } from '@react-google-maps/api/dist/utils/make-load-script-url';

// array ou objeto: manualmente informar o tipo da variavel

interface Item {
  id: number;
  title: string;
  image_url: string;
}

interface IBGEUfResponse {
  sigla: string;
}

interface IBGECityResponse {
  nome: string;
}
let latitude: String
const CreatePoint = () => {

  const [itens, setItens] = useState<Item[]>([]);
  const [ufs, setUfs] = useState<string[]>([]);
  const [cities, setCities] = useState<string[]>([]);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    Whatsapp: ''
  });

  const [selectedUf, setSelectedUf] = useState('0');
  const [selectedCity, setSelectedCity] = useState('0');
  const [selectedItens, setSelectedItens] = useState<Number[]>([]);
  const [selectedPosition, setSelectedPosition] = useState({lat: 0, lng:0});
  const [initialPosition, setInitialPosition] = useState({latitude: 0, longitude: 0});

  const containerStyle = {
    width: '600px',
    height: '400px',
    margin: '10px',
  };

  const center = {
    lat: -23.5381092,
    lng: -46.468151
  };
 
  useEffect(() => {
    api.get('itens').then(response => {
      setItens(response.data)
    })
  }, [])

  useEffect(() => {
    axios.get<IBGEUfResponse[]>('https://servicodados.ibge.gov.br/api/v1/localidades/estados').then(response => {
      const ufInitials = response.data.map(uf => uf.sigla);
      setUfs(ufInitials);
    })
  }, []);

  useEffect(() => {
    if (selectedUf === '0') {
      return;
    }
    axios
      .get<IBGECityResponse[]>(`https://servicodados.ibge.gov.br/api/v1/localidades/estados/${selectedUf}/municipios`)
      .then(response => {
        const cityNames = response.data.map(city => city.nome);
        setCities(cityNames);
      })
  }, [selectedUf]);

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(position => {
      console.log(position)
      const { latitude, longitude} = position.coords;

      setInitialPosition({latitude,longitude});
    })
  },[]);


  function handleInputChange(event: ChangeEvent<HTMLInputElement>) {
    const { name, value} = event.target;

    setFormData({ ...formData, [name]: value})
  }

  function handleSelectedUf(event: ChangeEvent<HTMLSelectElement>) {
    const uf = event.target.value;
    setSelectedUf(uf);
  }

  function handleSelectedCity(event: ChangeEvent<HTMLSelectElement>) {
    const city = event.target.value;
    setSelectedCity(city);
  }

  function handleMapClick(event: google.maps.MouseEvent) {
    let lat = event.latLng.lat();
    let lng = event.latLng.lng();
 
    setSelectedPosition({lat, lng});
  }

  function handleSelectItem(id: number) {
    const alreadySelected = selectedItens.findIndex(item => item === id);

    if (alreadySelected >= 0) {
      const filteredItens = selectedItens.filter(item => item !== id);
      setSelectedItens(filteredItens);
    } else {
      setSelectedItens([ ...selectedItens, id]);
    }
  }

  function handleSubmit() {
    
  }

  return (
    <div id="page-create-point">
      <header>
        <img src={logo} alt="Ecoleta" />
        <Link to="/">
          <FiArrowLeft />
                    Voltar para Home
                </Link>
      </header>

      <form onSubmit={handleSubmit}>
        <h1>Cadastro do <br /> Ponto de Coleta</h1>
        <fieldset>
          <legend>
            <h2>Dados</h2>
          </legend>
          <div className="field">
            <label htmlFor="name">Nome da entidade</label>
            <input
              type="text"
              name="name"
              id="name"
              onChange={handleInputChange}
            />
          </div>
          <div className="field-group">
            <label htmlFor="email">E-mail</label>
            <input
              type="email"
              name="email"
              id="email"
              onChange={handleInputChange}
            />
          </div>
          <div className="field">
            <label htmlFor="Whatsapp">Whatsapp</label>
            <input
              type="text"
              name="Whatsapp"
              id="Whatsapp"
              onChange={handleInputChange}
            />
          </div>
        </fieldset>
        <fieldset>
          <legend>
            <h2>Endereço</h2>
            <span>Selecione o endereço no mapa</span>
          </legend>
          <LoadScript
            googleMapsApiKey="AIzaSyARHdF5iy5i8irGDvISCfimw1lJVvY3qBQ"
          >
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={center}
              zoom={15}
              onClick={handleMapClick} 
            >
            {<Marker position={selectedPosition} />}
            
            </GoogleMap>
          </LoadScript>
          <div className="field-group">
            <div className="field">
              <label htmlFor="uf">Estado (UF)</label>
              <select
                name="uf"
                id="uf"
                value={selectedUf}
                onChange={handleSelectedUf}>
                <option value="0">Selecione uma UF</option>
                {ufs.map(uf => (
                  <option key={uf} value={uf}>{uf}</option>
                ))}
              </select>
            </div>
            <div className="field">
              <label htmlFor="uf">Cidade</label>
              <select
                name="city"
                id="city"
                onChange={handleSelectedCity}
              >
                <option value="0">Selecione uma cidade</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>
          </div>
        </fieldset>
        <fieldset>
          <legend>
            <h2>Ítens de Coleta</h2>
            <span>Selecione um ou mais ítens abaixo</span>
          </legend>
          <ul className="items-grid">
            {itens.map(item => (
              <li 
                key={item.id} 
                className={selectedItens.includes(item.id) ? 'selected' : ''} 
                onClick={() => handleSelectItem(item.id)}
              >
                <img src={item.image_url} alt={item.title} />
                <span>{item.title}</span>
              </li>
            ))}
          </ul>
        </fieldset>
        <button type="submit">Cadastrar ponto de coleta</button>
      </form>
    </div>
  );
}

export default CreatePoint;
